$(window).on('scroll', function() {
  if($(window).scrollTop() > 64) {
    $('header md-toolbar').addClass('active');
    $('.scroll-to-top-btn-container').show();
  } else {
    $('header md-toolbar').removeClass('active');
    $('.scroll-to-top-btn-container').hide();
  }
});

var app = angular.module('k7App', ['ngMaterial']);

app.controller('k7AppCtrl', function($scope, $mdSidenav) {
  $scope.currentYear = new Date().getFullYear();

  $scope.toggleMenu = function(e) {
    $mdSidenav('menu').toggle();
  };
});
